function translatePigLatin(str) {
  const regEx = /[aeiou]/;
  const indexOfVowel = str.search(regEx);
  if (indexOfVowel === str.length - 1) return `${str}way`;
  if (indexOfVowel === -1) return `${str}ay`;
  const partOne = str.slice(0, indexOfVowel);
  const partTwo = str.slice(str.search(regEx));
  if (0 === partOne.length) return `${str}way`;
  return `${partTwo}${partOne}ay`;
}

translatePigLatin("consonant");
