function convertHTML(str) {
  // &colon;&rpar;
  return str.replace(/<|>|&|"|'/g, (str,offset,s) => {
    switch(str) {
      case '&':
        return '&amp;';
      case '>':
        return '&gt;';
      case '<':
        return '&lt;';
      case '"':
        return '&quot;';
      case "'":
        return '&apos;';
    }
  });
}

convertHTML("Dolce & Gabbana");
