function sumFibs(num) {
  let prev = 1;
  let current = 1;
  let next;
  const fib = [1,1];
  while (current < num) {
    next = current + prev;
    if (next > num) break;
    fib.push(next);
    prev = current;
    current = next;
  }
  return fib.filter(item => item%2 !== 0).reduce((acc, item) => acc + item);
}

sumFibs(4);
