function sumPrimes(num) {
  const primes = [2];
  for(let i=3; i <= num; i++) {
    let divisible = false;
    let j = 2;
    for(;j < i; j++) {
      if (i%j === 0) divisible = true;
    }
    if (!divisible) primes.push(j);
    divisible = false;
  }
  return primes.reduce((acc, current) => acc + current);
}

