function smallestCommons(arr) {
  const sortedArr = arr.sort((a, b) => a > b);
  const startValue = sortedArr[0];
  const endValue = sortedArr[1];
  for(let i = 2; i < Number.MAX_SAFE_INTEGER;  i++) {
    const scm = endValue * i;
    let found = true;
    for(let j = endValue - 1;  j >= startValue; j--) {
      if (scm%j !== 0) {
        found = false;
        break;
      }
    }
    if (found) return scm;
  }
  return arr;
}


