function dropElements(arr, func) {
  // Drop them elements.
  const result = arr.slice(0);
  for(let i=0; i < arr.length; i++) {
    if (!func(arr[i])) {
      result.shift();
    } else {
      return result;
    }
  }
  return result;
}

dropElements([1, 2, 3], function(n) {return n < 3; });
