function convertToRoman(num) {
  const romans = {
    1: 'I',
    2: 'II',
    3: 'III',
    4: 'IV',
    5: 'V',
    6: 'VI',
    7: 'VII',
    8: 'VIII',
    9: 'IX',
    10: 'X',
    20: 'XX',
    30: 'XXX',
    40: 'XL',
    50: 'L',
    60: 'LX',
    70: 'LXX',
    80: 'LXXX',
    90: 'XC',
    100: 'C',
    200: 'CC',
    300: 'CCC',
    400: 'CD',
    500: 'D',
    600: 'DC',
    700: 'DCC',
    800: 'DCCC',
    900: 'CM',
    1000: 'M'
  };
  // 3136 => [6,3,1,3]
  const nums = String(num).split('').reverse().map(num => Number(num));
  const num2rom = nums.map((num, index, arr) => {
    switch(index) {
      case 3:
        return "M".repeat(num);
      case 2:
        return romans[num*100];
      case 1:
         return romans[num*10]
      case 0:
        return romans[num];
    }
});
  return num2rom.reverse().join('');
}

convertToRoman(36);
