function rot13(str) { // LBH QVQ VG!
    return str
    .split('')
    .map(char => {
        const charCode = char.charCodeAt(0);
        if (charCode >= 65 && charCode < 78) {
            return String.fromCharCode(charCode+13);
        } else if (charCode >= 78 && charCode <= 90) {
            return String.fromCharCode(charCode-13);
        } else {
            return char;
        }
    }).join('');
}

